DROP DATABASE IF EXISTS `DragonEducatorBot`;

CREATE DATABASE `DragonEducatorBot` DEFAULT CHARACTER SET utf8;
USE `DragonEducatorBot`;

CREATE TABLE `info` (
  `id` INT AUTO_INCREMENT,
  `key` TEXT NOT NULL,
  `value` TEXT NOT NULL,
  PRIMARY KEY (`id`)
) DEFAULT CHARACTER SET utf8;

CREATE TABLE `Items` (
  `id` INT,
  `name` TEXT DEFAULT NULL,
  `rarity` VARCHARACTER(2) DEFAULT NULL,
  `description` TEXT DEFAULT NULL,
  `value` INT DEFAULT NULL,
  `max_value` INT DEFAULT NULL,
  `estimate` INT DEFAULT NULL,
  `spread` DOUBLE DEFAULT NULL,
  `spread_tot` DOUBLE DEFAULT NULL,
  `craftable` TINYINT(1) DEFAULT 0,
  `reborn` INT DEFAULT NULL,
  `power` INT DEFAULT NULL,
  `power_armor` INT DEFAULT NULL,
  `power_shield` INT DEFAULT NULL,
  `dragon_power` INT DEFAULT NULL,
  `critical` INT DEFAULT NULL,
  `category` INT DEFAULT NULL,
  `cons` TINYINT(1) DEFAULT 0,
  `allow_sell` TINYINT(1) DEFAULT 0,
  `rarity_name` TEXT DEFAULT NULL,
  `craft_pnt` INT DEFAULT NULL,
  `cons_val` DOUBLE DEFAULT NULL,
  PRIMARY KEY (`id`)
) DEFAULT CHARACTER SET utf8;

CREATE TABLE `Recipes` (
  `id` INT,
  `material_1` INT NOT NULL,
  `material_2` INT NOT NULL,
  `material_3` INT NOT NULL,
  `material_result` INT NOT NULL,
  `command` TEXT NOT NULL,
  PRIMARY KEY (`id`),
  FOREIGN KEY (`material_1`)
	REFERENCES `Items`(`id`)
	ON UPDATE CASCADE
	ON DELETE CASCADE,
  FOREIGN KEY (`material_2`)
	REFERENCES `Items`(`id`)
	ON UPDATE CASCADE
	ON DELETE CASCADE,
  FOREIGN KEY (`material_3`)
	REFERENCES `Items`(`id`)
	ON UPDATE CASCADE
	ON DELETE CASCADE,
  FOREIGN KEY (`material_result`)
	REFERENCES `Items`(`id`)
	ON UPDATE CASCADE
	ON DELETE CASCADE
) DEFAULT CHARACTER SET utf8;

CREATE TABLE `Chats` (
  `id` BIGINT,
  `type` TEXT NOT NULL,
  `title` TEXT DEFAULT NULL,
  `username` VARCHAR(32) UNIQUE DEFAULT NULL,
  `first_name` TEXT DEFAULT NULL,
  `last_name` TEXT DEFAULT NULL,
  `invite_link` TEXT DEFAULT NULL,
  `welcome` LONGTEXT DEFAULT NULL,
  `domain` ENUM('all', 'exarch', 'princeps', 'creator') DEFAULT 'all' NOT NULL,
  `type_into_guild` ENUM('other', 'games', 'utility', 'team') DEFAULT 'other' NOT NULL,
 PRIMARY KEY (`id`)
) DEFAULT CHARACTER SET utf8;

CREATE TABLE `Players` (
  `id` BIGINT,
  `first_name` TEXT DEFAULT NULL,
  `last_name` TEXT DEFAULT NULL,
  `username` VARCHAR(32) UNIQUE DEFAULT NULL,
  `phone_number` TEXT DEFAULT NULL,
  `domain` ENUM('all', 'exarch', 'princeps', 'creator') DEFAULT 'all' NOT NULL,
  PRIMARY KEY (`id`)
) DEFAULT CHARACTER SET utf8;

CREATE TABLE `Statistics` (
  `id` BIGINT,
  `ability` INT DEFAULT 0 NOT NULL,
  `craft_points` INT DEFAULT 0 NOT NULL,
  `dragon` INT DEFAULT 0 NOT NULL,
  `experience` INT DEFAULT 0 NOT NULL,
  `rank` INT DEFAULT 0 NOT NULL,
  `weekly_craft_points` INT DEFAULT 0 NOT NULL,
  PRIMARY KEY (`id`),
  FOREIGN KEY (`id`)
	REFERENCES `Players`(`id`)
	ON UPDATE CASCADE
	ON DELETE CASCADE
) DEFAULT CHARACTER SET utf8;

CREATE TABLE `Backpacks` (
  `user_id` BIGINT,
  `item_id` INT,
  `quantity` INT DEFAULT 1 NOT NULL,
  PRIMARY KEY (`user_id`, `item_id`),
  FOREIGN KEY (`user_id`)
	REFERENCES `Players`(`id`)
	ON UPDATE CASCADE
	ON DELETE CASCADE,
  FOREIGN KEY (`item_id`)
	REFERENCES `Items`(`id`)
	ON UPDATE CASCADE
	ON DELETE CASCADE
) DEFAULT CHARACTER SET utf8;

CREATE TABLE `Smuggler` (
  `id` BIGINT,
  `booked_from` BIGINT DEFAULT NULL,
  `free` TINYINT(1) DEFAULT 0,
  `private` TINYINT(1) DEFAULT 0,
  PRIMARY KEY (`id`),
  FOREIGN KEY (`id`)
	REFERENCES `Players`(`id`)
	ON UPDATE CASCADE
	ON DELETE CASCADE,
  FOREIGN KEY (`booked_from`)
	REFERENCES `Players`(`id`)
	ON UPDATE CASCADE
	ON DELETE CASCADE
) DEFAULT CHARACTER SET utf8;

CREATE TABLE `Nest` (
  `id` BIGINT,
  `level` INT DEFAULT 0 NOT NULL,
  `missing_points` INT DEFAULT 21000 NOT NULL,
  `objective` INT DEFAULT 300 NOT NULL,
  PRIMARY KEY (`id`),
  FOREIGN KEY (`id`)
	REFERENCES `Players`(`id`)
	ON UPDATE CASCADE
	ON DELETE CASCADE
) DEFAULT CHARACTER SET utf8;

CREATE TABLE `Nest_transaction` (
  `donor_id` BIGINT,
  `receiver_id` BIGINT,
  `quantity` INT DEFAULT 1 NOT NULL,
  PRIMARY KEY (`donor_id`, `receiver_id`),
  FOREIGN KEY (`donor_id`)
	REFERENCES `Players`(`id`)
	ON UPDATE CASCADE
	ON DELETE CASCADE,
  FOREIGN KEY (`receiver_id`)
	REFERENCES `Players`(`id`)
	ON UPDATE CASCADE
	ON DELETE CASCADE
) DEFAULT CHARACTER SET utf8;


